(250,1,'2024-02-24 13:18:53','2024-02-24 13:18:53','<!-- wp:list -->\n<ul><!-- wp:list-item -->\n<li><a href=\"https://michaelkentburns.com/index.php/how-to/how-to-contribute-to-this-site/\" data-type=\"page\" data-id=\"115\">Contribute to this site</a></li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><a href=\"https://michaelkentburns.com/index.php/how-to-administer-a-wordpress-site/\" data-type=\"link\" data-id=\"https://michaelkentburns.com/index.php/how-to-administer-a-wordpress-site/\">WordPress administrative tasks</a></li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>Cloudways hosting administration tasks:</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>Find your way around this site:</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li><a href=\"https://michaelkentburns.com/index.php/using-xampp-for-development-work/\" data-type=\"page\" data-id=\"247\">Install a full stack on your local development computer</a></li>\n<!-- /wp:list-item --></ul>\n<!-- /wp:list -->','How to ...','','inherit','closed','closed','','108-revision-v1','','','2024-02-24 13:18:53','2024-02-24 13:18:53','',108,'https://michaelkentburns.com/?p=250',0,'revision','',0),
